import 'package:bloc/bloc.dart';import 'package:meta/meta.dart';import 'package:mytexnomart/data/source/model/data/special_product_item_data.dart';import '../../utils/hive_helper.dart';part 'basket_event.dart';part 'basket_state.dart';class BasketBloc extends Bloc<BasketEvent, BasketState> {  BasketBloc() : super(BasketInitial()) {    on<GetAllProductsFromBasketEvent>((event, emit) async {      final data = HiveHelper.getAllProducts();      print(HiveHelper.getAllProducts().length);      emit(GetAllProductsFromBasketState(data));    });    on<DeleteProductFromBasketEvent>((event, emit) async {      HiveHelper.deleteProducts(event.product);      final data = HiveHelper.getAllProducts();      emit(GetAllProductsFromBasketState(data));    });    on<DeleteBasket>((event, emit) async {      HiveHelper.deleteBasket();      final data = HiveHelper.getAllProducts();      emit(GetAllProductsFromBasketState(data));    });  }}