import 'package:bloc/bloc.dart';import 'package:meta/meta.dart';import 'package:mytexnomart/data/source/remote/api_client.dart';import '../../data/source/model/location/shops_location.dart';import '../../di/di.dart';part 'map_list_event.dart';part 'map_list_state.dart';class MapListBloc extends Bloc<MapListEvent, MapListState> {  final api = getIt<ApiClient>();  MapListBloc() : super(MapListInitial()) {    on<GetAllMapListEvent>((event, emit) async {      final result = await api.getShopsLocation();      emit(GetAllMapListState(result));    });  }}