import 'package:flutter/material.dart';import 'package:flutter_svg/flutter_svg.dart';Widget locationItem( String region, String title, String hour,    void Function() onClick) {  return Column(    children: [      InkWell(      onTap: onClick,      child: Column(        crossAxisAlignment: CrossAxisAlignment.start,        children: [          Text(            region,            style: TextStyle(fontSize: 12),          ),          const SizedBox(height: 10),          Row(            crossAxisAlignment: CrossAxisAlignment.start,            children: [              SvgPicture.asset(                'assets/images/ic_loc.svg',                width: 25,                height: 25,              ),              const SizedBox(width: 10),              Column(                crossAxisAlignment: CrossAxisAlignment.start,                children: [                  SizedBox(                    width: 250,                    child: Text(                      title,                      maxLines: 2,                      style: TextStyle(fontSize: 12),                    ),                  ),                  const SizedBox(height: 15),                  Text(                    'Du-Ya($hour)',                    style: TextStyle(fontSize: 12),                  ),                ],              ),              const Spacer(),              const Icon(                Icons.arrow_forward_ios_rounded,                size: 20,              )            ],          ),          const Divider()        ],      )),    ],  );}